function InputsFields() {
    const [text, setText] = useState('');
    const [number, setNumber] = useState(1);
    const [error, setError] = useState('');
  
    const handleTextChange = (event) => {
      setText(event.target.value);
    };
  
    const handleNumberChange = (event) => {
      setNumber(event.target.value);
    };
  
    const handleSubmit = (event) => {
      event.preventDefault();
      if (text.trim() === '') {
        setError('Please enter a valid string');
        return;
      } else if (isNaN(number) || number <= 0) {
        setError('Please enter a positive number');
        return;
      }
      setError('');
      const repeatedText = text.repeat(number);
      console.log(repeatedText);
      // Display repeatedText in a new paragraph below the form
    };
  
    return (
      <div>
        <form onSubmit={handleSubmit}>
          <label htmlFor="text-input">Enter a string:</label>
          <input
            id="text-input"
            type="text"
            value={text}
            onChange={handleTextChange}
          />
          <br />
          <label htmlFor="number-input">Enter a number:</label>
          <input
            id="number-input"
            type="number"
            value={number}
            onChange={handleNumberChange}
          />
          <br />
          <button type="submit">Submit</button>
        </form>
        <p style={{ color: 'red' }}>{error}</p>
      </div>
    );
  }

  export default InputsFields;
